import React, { useEffect, useRef, useState } from "react"
import { InputGroup, Form, Button, Card, Alert } from "react-bootstrap"
import { useAuth } from "./AuthContext"
import "./UpdateProfile.css"
import { Link, useHistory } from "react-router-dom"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye } from "@fortawesome/free-solid-svg-icons";
import { faEyeSlash } from "@fortawesome/free-solid-svg-icons";
import firebase from 'firebase/compat';
const eye = <FontAwesomeIcon icon={faEye} />;
const eyeclose = <FontAwesomeIcon icon={faEyeSlash} />;

export default function UpdateProfile() {
  const nameRef = useRef()
  const madhabRef = useRef()
  const countryRef = useRef()
  const cityRef = useRef()
  var name, country, city, madhab;
  const { currentUser } = useAuth()
  const [user, setUser] = useState()
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const [passwordShown, setPasswordShown] = useState(false)
  const history = useHistory()

  useEffectuseEffect(() => {
    var uid = currentUser.uid;
    var link = "http://127.0.0.1:3000/userprofile/".concat(uid);
    fetch(link)
      .then(res => res.json())
      .then(
        (result) => {
          setUser(result.data.users);
        }
      )
  }, []);

  function handleSubmit(e) {
    e.preventDefault()

    const promises = []
    setLoading(true)
    setError("")

    if (emailRef.current.value !== currentUser.email) {
      promises.push(updateEmail(emailRef.current.value))
    }
    if (passwordRef.current.value) {
      promises.push(updatePassword(passwordRef.current.value))
    }

    Promise.all(promises)
      .then(() => {
        history.push("/")
      })
      .catch(() => {
        setError("Failed to update account")
      })
      .finally(() => {
        setLoading(false)
      })
  }

  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  return (
    <>
      <Card className="emotionBox2">
        <Card.Body>
          <h1 className="text-center mb-4">Update Profile</h1>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group id="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                ref={nameRef}
                required
                defaultValue={user.name}
              />
            </Form.Group>
            <Form.Group id="name">
              <Form.Label>Madhab</Form.Label>
              
            </Form.Group>
            <Form.Group id="password-confirm">
              <Form.Label>Password</Form.Label>
              <Form.Control className="pass-wrapper" type={passwordShown ? "text" : "password"} ref={passwordConfirmRef} placeholder="Leave blank to keep the same" />
              <InputGroup.Append>
                <InputGroup.Text>
                  <i onClick={togglePasswordVisiblity}>{passwordShown ? eye : eyeclose}</i>
                </InputGroup.Text>
              </InputGroup.Append>
            </Form.Group>
            <Button disabled={loading} className="w-100" type="submit">
              Update
            </Button>
          </Form>
          <div className="w-100 text-center mt-2">
            <Link to="/">Cancel</Link>
          </div>
        </Card.Body>
      </Card>

    </>
  )
}
