import React, { useEffect, useRef, useState } from "react"
import { InputGroup, Form, Button, Card, Alert } from "react-bootstrap"
import { useAuth } from "./AuthContext"
import "./UpdateProfile.css"
import { Link, useHistory } from "react-router-dom"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye } from "@fortawesome/free-solid-svg-icons";
import { faEyeSlash } from "@fortawesome/free-solid-svg-icons";
import firebase from 'firebase/compat';
const eye = <FontAwesomeIcon icon={faEye} />;
const eyeclose = <FontAwesomeIcon icon={faEyeSlash} />;

export default function UpdateProfile() {
  const nameRef = useRef()
  const madhabRef = useRef()
  const countryRef = useRef()
  const cityRef = useRef()
  var name, country, city, madhab;
  const { currentUser } = useAuth()
  const [user, setUser] = useState()
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const [passwordShown, setPasswordShown] = useState(false)
  const history = useHistory()

  useEffect(() => {
    var uid = currentUser.uid;
    var link = "http://127.0.0.1:3000/userprofile/".concat(uid);
    fetch(link)
      .then(res => res.json())
      .then(
        (result) => {
          setUser(result.data.users);
        }
      )
  }, []);

  function handleSubmit(e) {
    e.preventDefault()

    const promises = []
    setLoading(true)
    setError("")

    if (user.name != nameRef || user.madhab != madhabRef || 
      user.country != countryRef || user.city != cityRef) {
      promises.push(
        fetch('http://localhost:3000/addmyday', {
            method: 'POST',
            body: JSON.stringify({ uid, task, isRecurred, isCompleted }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
      )
    }

    Promise.all(promises)
      .then(() => {
        history.push("/")
      })
      .catch(() => {
        setError("Failed to update account")
      })
      .finally(() => {
        setLoading(false)
      })
  }

  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  return (
    <>
      <Card className="emotionBox2">
        <Card.Body>
          <h1 className="text-center mb-4">Update Profile</h1>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group id="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                ref={nameRef}
                required
                defaultValue={user.name}
              />
            </Form.Group>
            <Form.Group id="name">
              <Form.Label>Madhab</Form.Label>
              <Form.Control
                type="text"
                ref={madhabRef}
                required
                defaultValue={user.madhab}
              />
            </Form.Group>
            <Form.Group id="name">
              <Form.Label>Country</Form.Label>
              <Form.Control
                type="text"
                ref={countryRef}
                required
                defaultValue={user.country}
              />
            </Form.Group>
            <Form.Group id="name">
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                ref={cityRef}
                required
                defaultValue={user.city}
              />
            </Form.Group>
            <Button disabled={loading} className="w-100" type="submit">
              Update
            </Button>
          </Form>
          <div className="w-100 text-center mt-2">
            <Link to="/">Cancel</Link>
          </div>
        </Card.Body>
      </Card>

    </>
  )
}
